plus ( id , title){
  let originalTitle = 'Rubic cube'
  if (title === originalTitle){
    this.products[id].quantity++
  }else {
    this.laps[id].quantity ++
  }
}

minus(id , title){
  let originalTitle = 'Rubic cube'
  if (title === originalTitle){
    this.products[id].quantity--
  }else {
    this.laps[id].quantity --
  }

}

